    test("signMessageByPayload", async () => {
        let wallet = new AptosWallet()
        const messagePayload: SignMessagePayload = {
            address: true,
            application: true,
            dAppDomain: "dapp",
            chainId: true,
            chain_id: 1,
            message: "Welcome to dApp!",
            nonce: "9999"
        }
        let signParams: SignMessageByPayloadParams = {
            privateKey: "0x4a6d287353203941768551f66446d5d4a85ab685b5b444041801014ae39419b5067aec3603bdca82e52a172ec69b2505a979f1d935a59409bacae5c7f268fc26",
            data: messagePayload
        };
        let res = await wallet.signMessageByPayload(signParams);
        expect(res.signature).toBe("a8f9685b42789c864ffc836b468e0fd1cf6c4edc96dd871f185a96ca1a0d3f315ce0de76beb186db5523bf9a300ce0f54234c90f745f4c35c89454f025ed6808")
    });

